import React, { useState } from "react";
import { Locations } from "./components/Locations";
import { PokemonData } from "./components/PokemonData";


const App = () => {
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [encounterEnded, setEncounterEnded] = useState(true);
  const [ourPokemons, setOurPokemons] = useState(null);

  // starting pokemons
  const usersPokemon = [
    "https://pokeapi.co/api/v2/pokemon/bulbasaur",
    "https://pokeapi.co/api/v2/pokemon/charizard",
    "https://pokeapi.co/api/v2/pokemon/poliwhirl",
  ];
 
  let usersPokemons = [];

  usersPokemon.map(async (pokeApi) => {
    const pokeApiResponse = await fetch(pokeApi);
    const pokeApiData = await pokeApiResponse.json();
    usersPokemons.push(pokeApiData)
  });
  setOurPokemons(usersPokemons);


  const handleLocationSelect = (location) => {
    setSelectedLocation(location);
    setEncounterEnded(false);
  };

  const handleEncounterEnd = () => {
    setSelectedLocation(null);
    setEncounterEnded(true);
  };



  return (
    <div>
      {encounterEnded ? (
        <Locations onSelect={handleLocationSelect} />
      ) : (
        <PokemonData
          locationUrl={selectedLocation}
          setEncounterEnded={handleEncounterEnd}
          o
        />
      )}
    </div>
  );
};

export default App;
